import { Component } from '@angular/core';
import { Router } from '@angular/router';
import { NavigationService } from '../../services/navigation.service';

@Component({
  selector: 'app-page4',
  templateUrl: './page4.component.html',
  styleUrls: ['./page4.component.css']
})
export class Page4Component {

  constructor(private router: Router, private navigationService: NavigationService) { }

  navigateToPage(page: string, buttonName: string): void {
    this.navigationService.recordButtonClick(buttonName);
    this.router.navigate([`/${page}`]);
  }

  logout(): void {
    console.log('User logged out');
    sessionStorage.removeItem('userId');
    this.router.navigate(['/login']);
  } goToUpdates(): void {
    this.router.navigate(['/navigation']);
  }
}

<!-- page4.component.html -->
<div>
  <h1>Dördüncü Sayfa</h1>
  <button (click)="navigateToPage('page1', 'buttonPage1')">Go to Page 1</button><br>
  <button (click)="navigateToPage('page2', 'buttonPage2')">Go to Page 2</button><br>
  <button (click)="navigateToPage('page3', 'buttonPage3')">Go to Page 3</button><br>
  <button (click)="logout()">Çıkış Yap</button><br>
</div>
