import { Component } from '@angular/core';
import { Router } from '@angular/router';
import { AuthService } from '../../services/auth.service'; // AuthService'in doğru yolda olduğundan emin olun

@Component({
  selector: 'app-admin-login',
  templateUrl: './admin-login.component.html',
  styleUrls: ['./admin-login.component.css']
})
export class AdminLoginComponent {
  constructor(private router: Router, private authService: AuthService) {}

  onLogin(username: string, password: string) {
    if (!username || !password) {
      console.error('Username and password are required');
      return;
    }

    this.authService.login({ username, password }).subscribe(
      (response: any) => {
        const token = response.token;
        const userId = response.id;

        // Token'ı saklayın
        this.authService.setToken(token);

        // Admin olarak başarılı giriş yapıldığında yönlendirme
        if (username === 'admin' && password === 'admin') {
          this.router.navigate(['/page5']);
        } else {
          console.error('Invalid admin credentials');
        }
      },
      (error) => {
        console.error('Login failed', error);
      }
    );
  }
}

<div class="wrapper">
  <form>
    <h2>Admin Giriş</h2>
    <div class="input-field">
      <input type="text" #username required>
      <label>Kullanıcı Adı</label>
    </div>
    <div class="input-field">
      <input type="password" #password required>
      <label>Şifre</label>
    </div>
    <button type="button" (click)="onLogin(username.value, password.value)">Giriş Yap</button>
  </form>
</div>
