import { Injectable } from '@angular/core';
import { CanActivate, Router } from '@angular/router';

@Injectable({
  providedIn: 'root'
})
export class AuthGuard implements CanActivate {

  constructor(private router: Router) { }

  canActivate(): boolean {
    const token = sessionStorage.getItem('token'); // Token'ı almak için uygun yer

    if (token) {
      return true; // Token varsa sayfaya erişime izin ver
    } else {
      this.router.navigate(['/login']); // Token yoksa giriş sayfasına yönlendir
      return false;
    }
  }
}
