import { Component } from '@angular/core';
import { Router } from '@angular/router';
import { AuthService } from '../auth.service'; // AuthService'i import et

@Component({
  selector: 'app-admin-login',
  templateUrl: './admin-login.component.html',
  styleUrls: ['./admin-login.component.css']
})
export class AdminLoginComponent {
  message: string = '';

  constructor(private router: Router, private authService: AuthService) {}

  onLogin(username: string, password: string) {
    // Kullanıcı adı ve şifre ile giriş yap
    this.authService.login({ username, password }).subscribe({
      next: (response: any) => {
        // Giriş başarılı ise
        if (response.token) {
          this.authService.setToken(response.token); // Token'ı sakla
          sessionStorage.setItem('userId', response.id.toString()); // Kullanıcı ID'sini sakla
          this.router.navigate(['/page5']); // Sayfaya yönlendir
        } else {
          this.message = 'Invalid username or password';
        }
      },
      error: (error: any) => {
        console.error('Login error:', error);
        this.message = 'Login failed. Please check your credentials.';
      }
    });
  }
}
