import { Component, OnInit } from '@angular/core';
import { HttpClient } from '@angular/common/http';

@Component({
  selector: 'app-page5',
  templateUrl: './page5.component.html',
  styleUrls: ['./page5.component.css']
})
export class Page5Component implements OnInit {
  buttonStats: any[] = [];

  constructor(private http: HttpClient) { }

  ngOnInit(): void {
    // Kullanıcının ID'sini localStorage'dan alın (veya başka bir şekilde sağlayın)
    const userId = localStorage.getItem('userId');
    if (userId) {
      this.http.get<any[]>(`http://localhost:3000/api/button-stats/${userId}`).subscribe(
        data => this.buttonStats = data,
        error => console.error('Error fetching button stats:', error)
      );
    }
  }
}
