import { Injectable } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { Observable } from 'rxjs';
import { tap } from 'rxjs/operators';
import { jwtDecode } from 'jwt-decode'; // Doğru import kullanımı

@Injectable({
  providedIn: 'root'
})
export class AuthService {
  private apiUrl = 'http://localhost:4002/api/v1'; // API URL'iniz
  private tokenKey = 'auth-token';

  constructor(private http: HttpClient) {}

  // Kullanıcı giriş yapma
  login(user: any): Observable<any> {
    return this.http.post<any>(`${this.apiUrl}/login`, user);
  }

  // Admin giriş yapma
  adminLogin(admin: any): Observable<any> {
    return this.http.post<any>(`${this.apiUrl}/admin-login`, admin);
  }

  // Token'ı saklama
  setToken(token: string): void {
    localStorage.setItem(this.tokenKey, token);
  }

  // Token'ı alma
  getToken(): string | null {
    return localStorage.getItem(this.tokenKey);
  }

  // Token'ı decode etme
  decodeToken(token: string): any {
    return jwtDecode(token);
  }

  // Kullanıcı ID'sini alma
  getUserId(): string | null {
    const token = this.getToken();
    if (token) {
      const decodedToken = this.decodeToken(token);
      return decodedToken.id || null; // id yerine doğru anahtar kullanımı
    }
    return null;
  }

  // Token'ın geçerli olup olmadığını kontrol etme
  isTokenValid(): boolean {
    const token = this.getToken();
    if (!token) {
      return false;
    }
    const decodedToken = this.decodeToken(token);
    const expirationDate = new Date(decodedToken.exp * 1000);
    return expirationDate > new Date();
  }

  // Çıkış yapma
  logout(): Observable<void> {
    return this.http.post<void>(`${this.apiUrl}/logout`, {}).pipe(
      tap(() => {
        localStorage.removeItem(this.tokenKey);
        sessionStorage.removeItem('userId');
      })
    );
  }
}
