import { Component } from '@angular/core';
import { Router } from '@angular/router';
import { NavigationService } from '../../services/navigation.service';

@Component({
  selector: 'app-page2',
  templateUrl: './page2.component.html',
  styleUrls: ['./page2.component.css']
})
export class Page2Component {

  constructor(private router: Router, private navigationService: NavigationService) { }

  navigateToPage(page: string, buttonName: string): void {
    this.navigationService.recordButtonClick(buttonName);
    this.router.navigate([`/${page}`]);
  }

  logout(): void {
    console.log('User logged out');
    sessionStorage.removeItem('userId');
    this.router.navigate(['/login']);
  }
  goToUpdates(): void {
    this.router.navigate(['/navigation']);
  }
}
<div>
  <h1>İkinci Sayfa</h1>
  <div class="radio-inputs">
    <div class="radio">
      <input type="radio" id="page1" name="pages" (click)="navigateToPage('page1', 'buttonPage1')" />
      <label for="page1" class="name">Go to Page 1</label>
    </div>
    <div class="radio">
      <input type="radio" id="page3" name="pages" (click)="navigateToPage('page3', 'buttonPage3')" />
      <label for="page3" class="name">Go to Page 3</label>
    </div>
    <div class="radio">
      <input type="radio" id="page4" name="pages" (click)="navigateToPage('page4', 'buttonPage4')" />
      <label for="page4" class="name">Go to Page 4</label>
    </div>
  </div>
  <button (click)="logout()">Çıkış Yap</button><br>
</div>

<div class="cards">
  <div class="card red" (click)="navigateToPage('page1')">
    <p class="tip">Sayfa 1</p>
  </div>
  <div class="card blue" (click)="navigateToPage('page3')">
    <p class="tip">Sayfa 2</p>
  </div>
  <div class="card green" (click)="navigateToPage('page4')">
    <p class="tip">Sayfa 3 </p>
  </div>
</div>
