import { Component } from '@angular/core';
import { Router } from '@angular/router';
import { NavigationService } from '../../services/navigation.service';

@Component({
  selector: 'app-page1',
  templateUrl: './page1.component.html',
  styleUrls: ['./page1.component.css']
})
export class Page1Component {

  constructor(private router: Router, private navigationService: NavigationService) { }

  navigateToPage(page: string, buttonName: string): void {
    this.navigationService.recordButtonClick(buttonName);
    this.router.navigate([`/${page}`]);
  }

  logout(): void {
    console.log('User logged out');  this.navigationService.logoutAndRedirect();// Logout işlemini NavigationService üzerinden gerçekleştir
  }

  goToUpdates(): void {
    this.router.navigate(['/navigation']);
  }
}

<div>
  <span
data-active-content="🤨"
data-hover-content="🤯"
data-content="🤓"
class="💀"
></span>

  <div class="cards">
    <div class="card red" (click)="navigateToPage('page2', 'buttonPage2')">
      <p class="tip">Sayfa 2 e git</p>
    </div>
    <div class="card blue" (click)="navigateToPage('page3', 'buttonPage3')">
      <p class="tip">Sayfa 3 e git /p>
    </div>
    <div class="card green" (click)="navigateToPage('page4', 'buttonPage4')">
      <p class="tip">Sayfa4 e git</p>
    </div>
    <div class="card green" (click)="logout()">
      <p class="tip">Çıkış Yap</p>
    </div>
  </div>
</div>
