import { Component, OnInit } from '@angular/core';
import { NavigationService } from '../../services/navigation.service';

@Component({
  selector: 'app-navigation',
  templateUrl: './navigation.component.html',
  styleUrls: ['./navigation.component.css']
})
export class NavigationComponent implements OnInit {
  userButtonStats: any[] = [];
  users: any[] = [];

  constructor(private navigationService: NavigationService) { }

  ngOnInit(): void {
    this.loadUserButtonStats();
    this.loadUsers();
  }

  private loadUserButtonStats(): void {
    this.navigationService.getUserButtonStats().subscribe(data => {
      this.userButtonStats = data.sort((a, b) => new Date(b.entry_time).getTime() - new Date(a.entry_time).getTime());
    }, error => {
      console.error('Error fetching user button stats', error);
    });
  }

  private loadUsers(): void {
    this.navigationService.getUsers().subscribe(data => {
      this.users = data;
    }, error => {
      console.error('Error fetching users', error);
    });
  }
}
