import { Component } from '@angular/core';
import { Router } from '@angular/router';
import { NavigationService } from '../../services/navigation.service';

@Component({
  selector: 'app-page2',
  templateUrl: './page2.component.html',
  styleUrls: ['./page2.component.css']
})
export class Page2Component {

  constructor(private router: Router, private navigationService: NavigationService) { }

  navigateToPage(page: string, buttonName: string): void {
    this.navigationService.recordButtonClick(buttonName);
    this.router.navigate([`/${page}`]);
  }

  logout(): void {
    console.log('User logged out');
    sessionStorage.removeItem('userId');
    this.router.navigate(['/login']);
  }
  goToUpdates(): void {
    this.router.navigate(['/navigation']);
  }
}
<!DOCTYPE html>
<html lang="en">
<head>
    <meta charset="UTF-8">
    <meta name="viewport" content="width=device-width, initial-scale=1.0">
    <title>İkinci Sayfa</title>
    <link rel="stylesheet" href="page2.component.css"> <!-- Burada CSS dosyanızın adı ve konumu -->
</head>
<body>
    <div>
        <h1>İkinci Sayfa</h1>
        <div class="radio-inputs">
            <div class="radio">
                <input type="radio" id="page1" name="pages" onclick="navigateToPage('page1', 'buttonPage1')" />
                <label for="page1" class="name">Go to Page 1</label>
            </div>
            <div class="radio">
                <input type="radio" id="page3" name="pages" onclick="navigateToPage('page3', 'buttonPage3')" />
                <label for="page3" class="name">Go to Page 3</label>
            </div>
            <div class="radio">
                <input type="radio" id="page4" name="pages" onclick="navigateToPage('page4', 'buttonPage4')" />
                <label for="page4" class="name">Go to Page 4</label>
            </div>
        </div>
        <button onclick="logout()">Çıkış Yap</button><br>
    </div>

    <script>
        function navigateToPage(page, buttonId) {
            // Sayfaya yönlendirme işlemi burada yapılacak
            console.log(`Navigating to ${page} with button ${buttonId}`);
        }

        function logout() {
            // Çıkış yapma işlemi burada yapılacak
            console.log('Logging out');
        }
    </script>
</body>
</html>
