import { Component } from '@angular/core';
import { AuthService } from 'src/app/services/auth.service'; // AuthService'i uygun yoldan import edin

@Component({
  selector: 'app-admin-login',
  templateUrl: '../admin-login/admin-login.component.html',
  styleUrls: ['../admin-login/admin-login.component.css']
})
export class AdminLoginComponent {
  message: string = '';

  constructor(private authService: AuthService) { }

  adminLoginClick(username: string, password: string) {
    this.authService.adminLogin(username, password).subscribe(
      response => {
        // Başarılı giriş işlemi
        console.log('Admin giriş başarılı');
      },
      error => {
        // Hata durumu
        this.message = 'Giriş başarısız. Lütfen bilgilerinizi kontrol edin.';
      }
    );
  }
}
