import { Component } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { Router } from '@angular/router';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.css']
})
export class LoginComponent {
  username: string = '';
  password: string = '';
  message: string = '';

  constructor(private http: HttpClient, private router: Router) { }

  loginClick() {
    this.http.post<{ token: string }>('http://localhost:4002/api/login', { username: this.username, password: this.password })
      .subscribe({
        next: (response) => {
          // Token'ı localStorage'a kaydet
          localStorage.setItem('token', response.token);
          this.router.navigate(['/page1']);
        },
        error: (error) => {
          console.error('Login error:', error);
          this.message = 'Giriş başarısız: ' + error.message;
        }
      });
  }
}
<form (ngSubmit)="loginClick()">
  <label for="username">Username:</label>
  <input id="username" name="username" [(ngModel)]="username" type="text">

  <label for="password">Password:</label>
  <input id="password" name="password" [(ngModel)]="password" type="password">

  <button type="submit">Login</button>
</form>
