import { Injectable } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { Observable } from 'rxjs';

@Injectable({
  providedIn: 'root'
})
export class ButtonClickService {
  private apiUrl = 'http://localhost:4200/api/button-clicks'; // API URL'nizi buraya koyun

  constructor(private http: HttpClient) {}

  // Butona tıklama kaydını veritabanına gönderir
  recordButtonClick(userId: number, buttonId: number): Observable<any> {
    const payload = { userId, buttonId };
    return this.http.post<any>(this.apiUrl, payload);
  }

  // Kullanıcı tıklama verilerini alır
  getButtonClicks(userId: number): Observable<any> {
    return this.http.get<any>(`${this.apiUrl}/${userId}`);
  }
}
