import { Component } from '@angular/core';
import { Router } from '@angular/router';
import { AuthService } from '../auth.service';

@Component({
  selector: 'app-admin-login',
  templateUrl: './admin-login.component.html',
  styleUrls: ['./admin-login.component.css']
})
export class AdminLoginComponent {
  username: string = '';
  password: string = '';
  errorMessage: string = '';

  constructor(private authService: AuthService, private router: Router) {}

  onLogin(): void {
    this.authService.adminLogin({ username: this.username, password: this.password }).subscribe(
      response => {
        const token = response.token;
        this.authService.setToken(token);
        this.router.navigate(['/page5']); // Başarılı girişten sonra yönlendirme
      },
      error => {
        this.errorMessage = 'Giriş başarısız! Lütfen kullanıcı adınızı ve şifrenizi kontrol edin.';
      }
    );
  }
}
