import { Component } from '@angular/core';
import { Router } from '@angular/router';
import { LoginService } from '../../services/login.service';
import { HttpErrorResponse } from '@angular/common/http';
import { User } from '../../models/user.model';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.css']
})
export class LoginComponent {
  message: string = '';

  constructor(private loginService: LoginService, private router: Router) {}

  loginClick(username: string, password: string) {
    const user: User = {
      username: username,
      password: password
    };

    this.loginService.login(user).subscribe(
      (response: any) => {
        console.log('Login successful:', response);
        const userId = response.id; // Kullanıcı ID'sini alın
        const token = response.token; // Token'ı alın
        sessionStorage.setItem('userId', userId.toString()); // Kullanıcı ID'sini sessionStorage'da saklayın
        localStorage.setItem('auth-token', token); // Token'ı localStorage'da saklayın
        this.router.navigate(['/page1']); // Page1 sayfasına yönlendirin
      },
      (error: HttpErrorResponse) => {
        console.error('Login error:', error);
        this.message = 'Login failed. Please check your credentials.';
      }
    );
  }
}

<div class="wrapper">
  <form>
    <h2>Login</h2>
    <div class="input-field">
      <input type="text" #username required>
      <label>Kullanıcı Adı</label>
    </div>
    <div class="input-field">
      <input type="password" #password required>
      <label>Şifre</label>
    </div>
    <button type="button" (click)="login(username.value, password.value)">Giriş Yap</button><br>
    <div class="login">
      <p>Henüz üye değil misin? <a routerLink="/register">Kayıt Ol</a></p>
    </div><br>
    <div class="login">
      <p>Admin girişi <a routerLink="/admin-login">Admin girişi</a></p>
    </div><br>
  </form>
  <p>{{ message }}</p>
</div>
